mapper.select:
select count(*)
from tb_medicine
where medNo = #{medNo}

mapper.update:
update tb_medicine
set price=#{price},
    medCount=#{medCount},
    factoryAdd=#{factoryAdd},
    description=#{description}
where id = #{id}

mapper.select:
SELECT * FROM tb_medicine


        and  medCount = #{medCount}


        and medCount > #{medCount}


        and medCount < #{medCount}
